<?phpif(empty($_POST['senderEmail'])){	echo"Error: No email address found";	exit;}$senderName		= $_POST['senderName'];$senderEmail	= $_POST['senderEmail'];$senderPhone	= $_POST['senderPhone'];$senderMessage	= nl2br($_POST['senderMessage']);$headers  = "MIME-Version: 1.0\r\n"; $headers .= "Content-type: text/html; charset=iso-8859-1\r\n";////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////// 1) Edit the first line below to match your email address////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////$headers .= "From: miguelriveroloop@gmail.com<> \n";$headers .= "Reply-To: " . $senderEmail . "\n\n";////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////// 2) Then edit the following two lines to match your site name and email address////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////$siteName		= "miguelrivero.net";$to 			= "miguelriveroloop@gmail.com";$toSubject 		= "Message from $senderName via $siteName";$emailBody 		= "From: $senderName <br />				Email: $senderEmail <br /> 				Telefono:$senderPhone <br /> <br />				Message: <br />			  	$senderMessage			  	<br />";  $message 		= $emailBody;$ok = mail($to, $toSubject, $message, $headers);if($ok){	echo "returnValue=1";}else{	echo "returnValue=0";}?>